import React, { useState, useEffect } from 'react';
import Form from './Form';


function ArticleEdit (props) {


  const [state, setState]= useState ({title: '', content: ''})

  useEffect(() => {//obtiene valores de title y contain a editar
    fetch(`api/articles/${props.match.params.id}`)
      .then(response => response.json())
      .then((data) => {
        setState(data);
      })
      .catch(error => console.log('error', error));
  },[]);

 const handleSubmit = (event) => {
    event.preventDefault();
    fetch(`api/articles/${props.match.params.id}`, {
        method: 'PATCH',
        body: JSON.stringify(state),
        headers: { 'Content-Type': 'application/json' }
      })
      .then(response => response.json())
      .then(data => {
        props.history.push(`/articles/${state.id}`);
      })
      .catch(error => console.log('error', error));
  }

  const handleChange = (event) => {

    setState({title:title, content: content});

  }

  const handleCancel = () => {
    props.history.push(`/articles/${state.id}`);
  }


    const article = {
      title: state.title,
      content: state.content
    };
    const settings = {
      handleSubmit: handleSubmit,
      handleChange: handleChange,
      handleCancel: handleCancel,
      actionLabel: 'Update'
    };
    return (
      <div>
        <h1>Edit {state.title}</h1>
        <Form article={article} settings={settings} />
      </div>
    );

}

export default ArticleEdit;
