import React from 'react';
import axios from 'axios';

export default class ArticlesApi extends React.Component {
  state = {
    article: []
  }

  componentDidMount() {
    axios.get('api/articles')
      .then(res => {
        const article = res.id;
        this.setState({ article });
      })
  }

  render() {
    return (
      <ul>
        { this.state.article.map(article => <li>{article.name}</li>)}
      </ul>
    )
  }
}
